// Main seeding function
async function seedDatabase(config = {}) {
  console.log('ğŸŒ± Starting database seeding...')
  
  const {
    businessName = 'Demo Store',
    businessType = 'ecommerce',
    industry = 'technology'
  } = config

  try {
    const batch = writeBatch(db)
    
    // 1. Create categories
    console.log('ğŸ“‚ Creating categories...')
    const categoriesData = getCategories(businessType, industry)
    const categoryIds = []
    
    for (const categoryData of categoriesData) {
      const categoryRef = doc(collection(db, 'categories'))
      const category = {
        ...categoryData,
        active: true,
        productCount: 0,
        createdAt: new Date(),
        updatedAt: new Date()
      }
      batch.set(categoryRef, category)
      categoryIds.push({ id: categoryRef.id, ...category })
    }
    
    // Commit categories first
    await batch.commit()
    console.log(`âœ… Created ${categoryIds.length} categories`)
    
    // 2. Create products
    console.log('ğŸ“¦ Creating products...')
    const productsData = getProducts(businessType, industry, categoryIds)
    const productIds = []
    
    for (const productData of productsData) {
      const productRef = await addDoc(collection(db, 'products'), productData)
      productIds.push({ id: productRef.id, ...productData })
    }
    console.log(`âœ… Created ${productIds.length} products`)
    
    // 3. Create sample customers
    console.log('ğŸ‘¥ Creating customers...')
    const customersData = getSampleCustomers()
    const customerIds = []
    
    for (const customerData of customersData) {
      const customerRef = await addDoc(collection(db, 'customers'), customerData)
      customerIds.push({ id: customerRef.id, ...customerData })
    }
    console.log(`âœ… Created ${customerIds.length} customers`)
    
    // 4. Create sample orders
    console.log('ğŸ›’ Creating orders...')
    const ordersData = getSampleOrders(productIds)
    const orderIds = []
    
    for (const orderData of ordersData) {
      const orderRef = await addDoc(collection(db, 'orders'), orderData)
      orderIds.push({ id: orderRef.id, ...orderData })
    }
    console.log(`âœ… Created ${orderIds.length} orders`)
    
    // 5. Create sample reviews
    console.log('â­ Creating reviews...')
    const reviewsData = [
      {
        productId: productIds[0]?.id,
        customerId: customerIds[0]?.id,
        customerName: 'John Doe',
        customerEmail: 'john.doe@example.com',
        rating: 5,
        title: 'Excellent product!',
        content: 'This product exceeded my expectations. Great quality and fast shipping.',
        approved: true,
        helpful: 3,
        createdAt: new Date()
      },
      {
        productId: productIds[0]?.id,
        customerId: customerIds[1]?.id,
        customerName: 'Jane Smith',
        customerEmail: 'jane.smith@example.com',
        rating: 4,
        title: 'Good value',
        content: 'Nice product for the price. Would recommend to others.',
        approved: true,
        helpful: 1,
        createdAt: new Date()
      }
    ]
    
    for (const reviewData of reviewsData) {
      await addDoc(collection(db, 'reviews'), reviewData)
    }
    console.log(`âœ… Created ${reviewsData.length} reviews`)
    
    console.log('ğŸ‰ Database seeding completed successfully!')
    console.log(`
ğŸ“Š Summary:
- Categories: ${categoryIds.length}
- Products: ${productIds.length}
- Customers: ${customerIds.length}
- Orders: ${orderIds.length}
- Reviews: ${reviewsData.length}
    `)
    
    return {
      success: true,
      data: {
        categories: categoryIds.length,
        products: productIds.length,
        customers: customerIds.length,
        orders: orderIds.length,
        reviews: reviewsData.length
      }
    }
    
  } catch (error) {
    console.error('âŒ Seeding failed:', error)
    return {
      success: false,
      error: error.message
    }
  }
}

// CLI usage
if (typeof window === 'undefined') {
  // Node.js environment (CLI)
  const config = {
    businessName: process.env.BUSINESS_NAME || 'Demo Store',
    businessType: process.env.BUSINESS_TYPE || 'ecommerce',
    industry: process.env.INDUSTRY || 'technology'
  }
  
  seedDatabase(config)
    .then(result => {
      if (result.success) {
        console.log('âœ… Seeding completed successfully')
        process.exit(0)
      } else {
        console.error('âŒ Seeding failed:', result.error)
        process.exit(1)
      }
    })
    .catch(error => {
      console.error('âŒ Seeding error:', error)
      process.exit(1)
    })
}

export default seedDatabase